def heatmap(frame):
    # Bottom,Left,Right,Top

    p = ggplot(frame, aes('Date', 'Artist')) + geom_tile(aes(fill='Spend')) \
        + scale_fill_gradientn(colors=['#cccccc','#a5a5a5','#898989','#6d6d6d', '#474747', '#2b2b2b', '#000000']) \
        + ggtitle("Audience") \
        + facet_grid(['Day', 'Audience']) \
        + theme(axis_text_x  = element_text(angle = 90, hjust = 1))
    p.save("testo.png")
    return p

